import React from 'react';
import { connect } from 'react-redux';
import {Form} from 'react-bootstrap';
import {Button} from 'react-bootstrap';
import {setDoingTasks} from '../../actions/tasks';

class AddTask extends React.PureComponent
{
  handleSubmit(e)
  {
    e.preventDefault();
    // const value=e.target.value;
    // // this.props.setData(value);
    // console.log(e.target.value);
  }
  handleChange(event)
  {
    event.preventDefault();
    const value=e.target.value;
    // this.props.setData(value);
    console.log(value);
  }
  render()
  {
      return(
          <>
        <Form className="AddTaskForm" onSubmit={this.handleSubmit}>
        <Form.Group controlId="formBasicEmail">
          <Form.Label>Enter New Task</Form.Label>
          <Form.Control type="text" placeholder="Enter Task" onChange={this.handleChange}/>
        </Form.Group>
        <Button variant="primary" type="submit" className="AddTaskForm_btn">
          Add
        </Button>
      </Form>
      </>
      )
  }
}

const mapStateToProps = state => ({
  data: state.data,
});


const mapDispatchToProps = dispatch => ({
  setData: data => dispatch(setDoingTasks(data)),
});


export default connect(mapStateToProps, mapDispatchToProps)(AddTask);
