import React from 'react';
import { connect } from 'react-redux';
import { Card } from "react-bootstrap";
import { Button } from "react-bootstrap";

import { deleteTask } from '../../Actions/Tasks';
import { completeTask } from '../../Actions/Tasks';



class Task extends React.PureComponent {
    constructor(props) {
        super(props);

        this.handleDelete = this.handleDelete.bind(this);
        this.handleComplete = this.handleComplete.bind(this);


    }
    handleDelete(e) {
            
    this.props.deleteTask(e.currentTarget.dataset.id);

    }
    handleComplete(e)
    {
    this.props.completeTask(e.currentTarget.dataset.id);
    }
    render() {

        const { data,id } = this.props;
        return (
            <>
                <Card className="Task_card">
                    <Card.Body style={{ display: 'flex' }}>
                        <Card.Text >{data}</Card.Text>
                        <Button variant="primary" className="Task_btn" style={{ backgroundColor: '#328A3B', borderColor: '#328A3B' }} data-id={id} onClick={this.handleComplete}>Done</Button>
                        <Button variant="primary" className="Task_btn" style={{ backgroundColor: '#D93738', borderColor: '#D93738' }} data-id={id} onClick={this.handleDelete}>Delete</Button>
                    </Card.Body>
                </Card>
            </>
        )
    }
}


const mapDispatchToProps = dispatch => ({
    deleteTask: id => dispatch(deleteTask(id)),
    completeTask: id => dispatch(completeTask(id))
});


export default connect(null, mapDispatchToProps)(Task);
