import React from 'react';
import { Card } from "react-bootstrap";
import { Button } from "react-bootstrap";
import { MyContext } from '../../App';


class Task extends React.PureComponent {


    // constructor(props) {
    //     super(props);
    //     this.state = {
    //         DoneTasks: []
    //     }
    //     // this.handleDelete = this.handleDelete.bind(this);
    //     // this.handleComplete = this.handleComplete.bind(this);


    // }

    // handleDelete(e) {
    //     // this.setState({ showDone: false });
    //     this.props.deleteTask(e.currentTarget.dataset.id);

    // }
    handleComplete= (data,updateData) => (e) => {
        // this.setState({ btnTxt: 'Undone' });
        // this.setState({DoneTasks: data.slice()});
        // debugger;
        const copy=data.slice();
        copy.forEach(t => {
          if(t.id===e.currentTarget.dataset.id)
          {
           if(t.completed==false)
           {
            t.completed=true;
           }
          }
        });
        
        updateData(copy);

        // return {...state, doingTasks:copyState};
        // this.props.completeTask(e.currentTarget.dataset.id);
    }
    render() {

        const { body, id } = this.props;
        const Donebtntxt = this.props.completed ? 'Undo' : 'Done';
        const Deletebtntxt = this.props.deleted ? 'Undelete' : 'Delete';

        return (
            <MyContext.Consumer>
                {value =>
                    (

                        <>
                            <Card className="Task_card">
                                <Card.Body style={{ display: 'flex' }}>
                                    <Card.Text >{body}</Card.Text>
                                    {!this.props.deleted && <Button variant="primary" className="Task_btn" style={{ backgroundColor: '#328A3B', borderColor: '#328A3B' }} data-id={id} onClick={this.handleComplete(value.state.data,value.updateData)}>{Donebtntxt}</Button>}
                                    <Button variant="primary" className="Task_btn" style={{ backgroundColor: '#D93738', borderColor: '#D93738' }} data-id={id} >{Deletebtntxt}</Button>
                                </Card.Body>
                            </Card>
                        </>
                    )
                }
            </MyContext.Consumer>

        )
    }
}




export default Task;
