import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import data from './data'
import * as serviceWorker from './serviceWorker';
import { Card } from 'react-bootstrap';
import { Button } from 'react-bootstrap';

// import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/css/bootstrap.css';
import 'bootstrap/dist/js/bootstrap.js';

const Movies = (props) => {
    let { data } = props;
    let movie = data.map(
        // a=><div><h3>{a.Title}</h3>
        //          <p>{a.Type}</p>
        //          <p>{a.Year}</p>
        //          <img src={a.Poster}></img>
        //     </div>
        a => <Card style={{ width: '19rem'}} class="card-style">
            <Card.Img  variant="top" src={a.Poster} />
            <Card.Body>
                <Card.Title class="card-title">{a.Title}</Card.Title>
                <Card.Text>
                  <p>{a.Year}</p>
                  <p>{a.Type}</p>
                </Card.Text>
                  <Button variant="primary">Go somewhere</Button>
            </Card.Body>
        </Card>
    );
    return movie


}
ReactDOM.render(<Movies data={data} />, document.getElementById('root'));

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: https://bit.ly/CRA-PWA
serviceWorker.unregister();
