import React from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import MovieCard from './card';

import { Form } from 'react-bootstrap';
import {Button} from 'react-bootstrap';
import {InputGroup} from 'react-bootstrap';

class Movies extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            searchName: "",
            current: this.props.data
            // input: ""
        };
        this.search = this.search.bind(this);
        this.getWatchedMovies = this.getWatchedMovies.bind(this);

    }
    
search(e){
  const target=e.target;
  let finalArray=  this.props.data.filter((element)=> element.Title.toLowerCase().includes(target.value.toLowerCase()));
  this.setState({searchName: target.value, current: finalArray});
  
}
getWatchedMovies(e)
{
    console.log(e);
  let finalArray=this.props.data.filter((element)=>element.watched===true);
  this.setState({current: finalArray});
}
render() {

    // let { data } = this.props;
    return (
        <>
            <Form.Control type="text" placeholder="Normal text" className="form-textbox" onChange={this.search}/>
            {/* <Button variant="primary" className="listBtn" onClick={this.getWatchedMovies}>Watched Movies</Button> */}
            <Form.Check type="checkbox" label="Watched Movies" className="form-checkbox" onChange={this.getWatchedMovies}/>
            <div className="cards">
            {
                
                this.state.current.map(
                    a => <MovieCard key={a.imdbID} {...a} />

                )
            }
            </div>
        </>
    )


}
}

export default Movies;
