import React from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import MovieCard from './card';

import { Form } from 'react-bootstrap';

class Movies extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            searchName: ""
            // input: ""
        };
        this.search = this.search.bind(this);
    }
    
search(e){
  const target=e.target;
  this.setState({searchName: target.value}, ()=> {console.log(this.state)});
  
}
render() {

    let { data } = this.props;

    for(let i=0;i<data.length;i++)
    {
    if(data[i].Title.contain(this.state.searchName))
    { 
       data.splice(i,1);

    }
    }
    return (
        <>
            <Form.Control type="text" placeholder="Normal text" className="form-textbox" onChange={this.search}/>
            {
                
                data.map(
                    a => <MovieCard key={a.imdbID} {...a} />

                )
            }
            
        </>
    )


}
}

export default Movies;
