import React from 'react';
import { Card } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.css';
// import data from './data'

const Movies = (props) => {
    let { data } = props;
    let movie = data.map(
        a => <Card key={a.imdbID} style={{ width: '19rem'}} className="card-style">
            <Card.Img  variant="top" src={a.Poster === 'N/A' ? 'http://ia.media-imdb.com/images/M/MV5BNjNhYzgzYTUtNmI5OS00OGNmLTk2OTQtOWU1ODA3MTAxMTIxXkEyXkFqcGdeQXVyNjUzOTQwNTc@._V1_SX300.jpg':a.Poster} />
            <Card.Body>
                <Card.Title className="card-title">{a.Title}</Card.Title>
                <Card.Text>
                  {a.Year}
                  {a.Type}
                </Card.Text>
            </Card.Body>
        </Card>

    );
    return movie


}
export default Movies;
