export default (state, action) => {
  // debugger;
    switch (action.type) {
      case 'SET_DOING_TASKS':
        return { ...state, doingTasks: state.doingTasks.push(action.doingTasks) };
      case 'SET_DONE_TASKS':
      return { ...state,  doneTasks: action.doneTasks };
      case 'SET_DELETED_TASKS':
        return { ...state, deletedTasks: action.deletedTasks };
      default:
        return state;
    }
  };
