import React from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import MovieCard from './card';

import { Form } from 'react-bootstrap';
import { Button } from 'react-bootstrap';
import { InputGroup } from 'react-bootstrap';

class Movies extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            searchName: "",
            current: this.props.data
            // input: ""
        };
        this.search = this.search.bind(this);
        this.getWatchedMovies = this.getWatchedMovies.bind(this);

    }

    search(e) {
        const target = e.target;
        var checked = e.target.checked;
        let finalArray;
        finalArray = this.props.data.filter((element) => element.Title.toLowerCase().includes(target.value.toLowerCase()));
        if(checked)
        {
            finalArray = finalArray.filter((element) => element.watched === true);

        }
        this.setState({ searchName: target.value, current: finalArray });

    }
    getWatchedMovies(e) {
        var checked = e.target.checked;
        let finalArray;
        if (checked) {
            finalArray = this.props.data.filter((element) => element.watched === true);
            this.setState({ current: finalArray });
        }
        else {
            finalArray = this.props.data;
            this.setState({ current: finalArray });
        }
     
    }
    render() {

        // let { data } = this.props;
        return (
            <>
                <div className="controls">
                <Form.Control type="text" placeholder="Normal text" className="form-textbox" onChange={this.search} />
                <div className="filters">
                 <span className="filters-title">Filters</span>
                <Form.Check type="checkbox" label="Watched Movies" className="form-checkbox" onChange={this.search} />
                </div>
                </div>
                <div className="cards">
                    {

                        this.state.current.map(
                            a => <MovieCard key={a.imdbID} {...a} />

                        )
                    }
                </div>
            </>
        )


    }
}

export default Movies;
