export default (state, action) => {
    switch (action.type) {
      case 'SET_DOING-TASKS':
        return { ...state, doingTasks: action.doingTasks };
      case 'SET_DONE-TASKS':
      return { ...state,  doneTasks: action.doneTasks };
      case 'SET_DELETED-TASKS':
        return { ...state, deletedTasks: action.deletedTasks };
      default:
        return state;
    }
  };
