import React from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import MovieCard from './card';

import { Form } from 'react-bootstrap';
import { Button } from 'react-bootstrap';
import { InputGroup } from 'react-bootstrap';

class Movies extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            searchName: "",
            current: this.props.data,
            checked: false
        };
        this.search = this.search.bind(this);
        this.getWatchedMovies = this.getWatchedMovies.bind(this);

    }
    
    search(e) {
        // debugger;
        const name= e.target.name;
        const value = e.target.type==='checkbox'? e.target.checked : e.target.value;
        this.setState({ [name]: value }, ()=>{
            let finalArray=this.props.data.filter(element=>element.Title.toLowerCase().includes(this.state.searchName) && this.state.checked === element.watched);
           this.setState({current: finalArray});
        });
        
        // if(value!="on")
        // {
        // finalArray = this.props.data.filter((element) => element.Title.toLowerCase().includes(value.toLowerCase()));
        // }
        // else
        // {
        //     finalArray=this.props.data;
        // }
        // if(checked)
        // {
        //     finalArray = finalArray.filter((element) => element.watched === true);

        // }
        // else
        // {
        //     finalArray= this.props.data.filter((element) => element.Title.toLowerCase().includes(searchName.toLowerCase()));
        // }

    }
    getWatchedMovies(e) {
        var checked = e.target.checked;
        let finalArray;
        if (checked) {
            finalArray = this.props.data.filter((element) => element.watched === true);
            this.setState({ current: finalArray });
        }
        else {
            finalArray = this.props.data;
            this.setState({ current: finalArray });
        }
     
    }
    render() {

        // let { data } = this.props;
        return (
            <>
                <div className="controls">
                <Form.Control type="text" placeholder="Normal text" className="form-textbox" onChange={this.search} name="searchName"/>
                <div className="filters">
                 <span className="filters-title">Filters</span>
                <Form.Check type="checkbox" label="Watched Movies" className="form-checkbox" onChange={this.search} name="checked" />
                </div>
                </div>
                <div className="cards">
                    {

                        this.state.current.map(
                            a => <MovieCard key={a.imdbID} {...a} />

                        )
                    }
                </div>
            </>
        )


    }
}

export default Movies;
