import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import data from './data'
import * as serviceWorker from './serviceWorker';
import { Card } from 'react-bootstrap';
import { Button } from 'react-bootstrap';

// import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/css/bootstrap.css';
// import 'bootstrap/dist/js/bootstrap.js';

const Movies = (props) => {
    let { data } = props;
    let movie = data.map(
        a => <Card key={a.Year} style={{ width: '19rem'}} className="card-style">
            <Card.Img  variant="top" src={a.Poster === 'N/A' ? 'http://ia.media-imdb.com/images/M/MV5BNjNhYzgzYTUtNmI5OS00OGNmLTk2OTQtOWU1ODA3MTAxMTIxXkEyXkFqcGdeQXVyNjUzOTQwNTc@._V1_SX300.jpg':a.Poster} />
            <Card.Body>
                <Card.Title className="card-title">{a.Title}</Card.Title>
                <Card.Text>
                  <p>{a.Year}</p>
                  <p>{a.Type}</p>
                </Card.Text>
            </Card.Body>
        </Card>
    );

    return movie


}
ReactDOM.render(<Movies data={data} />, document.getElementById('root'));

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: https://bit.ly/CRA-PWA
serviceWorker.unregister();
