export default (state, action) => {
  // debugger;
    switch (action.type) {
      case 'SET_DOING_TASKS':
        return { ...state, doingTasks: [...state.doingTasks,action.doingTasks] };
      case 'SET_DONE_TASKS':
      return { ...state,  doneTasks: action.doneTasks };
      case 'DELETE_TASK':
        return { ...state, deletedTasks:[...state.deletedTasks,action.deletedTasks]  };
      default:
        return state;
    }
  };
