import React from 'react';
import SimpleSchema from 'simpl-schema';
import uuidv1 from 'uuid/v1';
import { connect } from 'react-redux';
import { Form } from 'react-bootstrap';
import { Button } from 'react-bootstrap';
import { setDoingTasks } from '../../Actions/Tasks';
import { MyContext } from '../../App';

class AddTask extends React.PureComponent {
  constructor(props) {
    super(props);
    this.state = {
      data: ''
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);

  }
  handleSubmit(e) {
    e.preventDefault();
    if (!this.state.data) return;
    const task =
    {
      data: this.state.data, completed: false, deleted: false, id: uuidv1(),
    };
    this.props.setData(task);
    this.setState({ data: '' });

    // const {doingTasks} = this.props.data;
    // const validationContext=new SimpleSchema({
    //     doingTasks: {type:String, min:2},
    // }).newContext();
    // validationContext.validate({doingTasks});
    // console.log(validationContext.validationErrors());
  }
  handleChange(event) {

    event.preventDefault();
    const data = event.target.value;
    this.setState({ data: data });

  }
  render() {
    return (
      // <>
      //   <Form className="AddTaskForm" onSubmit={this.handleSubmit}>
      //     <Form.Group controlId="formBasicEmail">
      //       <Form.Label>Enter New Task</Form.Label>
      //       <Form.Control type="text" placeholder="Enter Task" onChange={this.handleChange} value={this.state.data} />
      //     </Form.Group>
      //     <Button style={{ marginLeft: '270px' }} variant="primary" type="submit" className="AddTaskForm_btn">
      //       Add
      //   </Button>
      //   </Form>
      // </>

      <MyContext.Consumer>
        {value => 
        (
          <>
          <Form className="AddTaskForm" onSubmit={this.handleSubmit}>
            <Form.Group controlId="formBasicEmail">
              <Form.Label>Enter New Task</Form.Label>
              <Form.Control type="text" placeholder="Enter Task" onChange={this.handleChange} value={this.state.data} />
            </Form.Group>
            <Button style={{ marginLeft: '270px' }} variant="primary" type="submit" className="AddTaskForm_btn">
              Add
          </Button>
          </Form>
        </>
        )
        }
      </MyContext.Consumer>
    )
  }
}

const mapStateToProps = state => ({
  doingTasks: state.doingTasks,
});


const mapDispatchToProps = dispatch => ({
  setData: doingTasks => dispatch(setDoingTasks(doingTasks)),
});


export default connect(mapStateToProps, mapDispatchToProps)(AddTask);
