export default (state, action) => {
  // debugger;
    switch (action.type) {
      case 'SET_DOING_TASKS':
        return { ...state, doingTasks: [...state.doingTasks,action.doingTasks] };
      case 'SET_DONE_TASKS':
      return { ...state,  doneTasks: action.doneTasks };
      case 'DELETE_TASK':
        {
          this.state.doingTasks.map(t => {
            if(t.id===action.id)
            {
              console.log(t);
              t.deleted=true;
            }
          })
          return {state};
        }
      default:
        return state;
    }
  };
