import React from 'react';
import { Card } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.css';

class MovieCard extends React.Component{
    render(){
        const {Poster, Title, Year,Type}=this.props;
        return(
            <Card  style={{ width: '19rem'}} className="mycard-style">
            <Card.Img  variant="top" src={Poster === 'N/A' ? 'http://ia.media-imdb.com/images/M/MV5BNjNhYzgzYTUtNmI5OS00OGNmLTk2OTQtOWU1ODA3MTAxMTIxXkEyXkFqcGdeQXVyNjUzOTQwNTc@._V1_SX300.jpg':Poster} />
            <Card.Body>
                <Card.Title className="card-title">{Title}</Card.Title>
                <Card.Text>
                  {Year}
                  {Type}
                </Card.Text>
            </Card.Body>
        </Card>
        );
    }
}

export default MovieCard;
