import React from 'react';
import { Card } from "react-bootstrap";
import { Button } from "react-bootstrap";

class Task extends React.PureComponent {
    render() {
        
        const {data} =this.props;
        return (
            <>
                <Card className="Task_card">
                    <Card.Body style={{display: 'flex'}}>
                        <Card.Text >{data}</Card.Text>
                        <Button variant="primary" className="Task_btn" style={{backgroundColor:'#328A3B',borderColor:'#328A3B'}}>Done</Button>
                        <Button variant="primary" className="Task_btn" style={{backgroundColor:'#D93738',borderColor:'#D93738'}} >Delete</Button>
                    </Card.Body>
                </Card>
            </>
        )
    }
}
export default Task;
